Option.create!([
  {name: "Add drop database", long_option: "--add-drop-database", short_option: "", option_file: "add-drop-database", short_option_can_be_strung: false, description: "Add a DROP DATABASE statement before each CREATE DATABASE statement\r\n", status: 1, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Add drop table", long_option: "--add-drop-table", short_option: "", option_file: "add-drop-table", short_option_can_be_strung: false, description: "Add a DROP TABLE statement before each CREATE TABLE statement\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: "true"},
  {name: "Add drop trigger", long_option: "--add-drop-trigger", short_option: "", option_file: "add-drop-trigger", short_option_can_be_strung: false, description: "Add a DROP TRIGGER statement before each CREATE TRIGGER statement\r\n", status: nil, tool_id: 1, introduced_version: nil, removed_version: nil, extended_description: nil, default_value: nil},
  {name: "Add locks", long_option: "--add-locks", short_option: "", option_file: "add-locs", short_option_can_be_strung: false, description: "Surround each table dump with LOCK TABLES and UNLOCK TABLES statements", status: nil, tool_id: 1, introduced_version: nil, removed_version: nil, extended_description: nil, default_value: nil},
  {name: "All databases", long_option: "--all-databases", short_option: "-A", option_file: "all-databases", short_option_can_be_strung: false, description: "Dump all tables in all databases\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "All tablespaces", long_option: "--all-tablespaces", short_option: "-Y", option_file: "all-tablespaces", short_option_can_be_strung: false, description: "Adds to a table dump all SQL statements needed to create any tablespaces used by an NDB Cluster table\r\n", status: nil, tool_id: 1, introduced_version: "5.1.6", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Allow keywords", long_option: "--allow-keywords", short_option: "", option_file: "allow-keywords", short_option_can_be_strung: false, description: "Allow creation of column names that are keywords\r\n", status: nil, tool_id: 1, introduced_version: nil, removed_version: nil, extended_description: nil, default_value: nil},
  {name: "Bind address", long_option: "--bind-address=ip_address", short_option: "", option_file: "bind-address", short_option_can_be_strung: false, description: "Use the specified network interface to connect to the MySQL Server", status: nil, tool_id: 1, introduced_version: "5.5.8", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Comments", long_option: "--comments", short_option: "", option_file: "comments", short_option_can_be_strung: false, description: "Add comments to the dump file\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Compact", long_option: "--compact", short_option: "", option_file: "compact", short_option_can_be_strung: false, description: "Produce more compact output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Compatible", long_option: "--compatible=name[,name,...]", short_option: "", option_file: "compatible", short_option_can_be_strung: false, description: "Produce output that is more compatible with other database systems or with older MySQL servers\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Complete insert", long_option: "--complete-insert", short_option: "", option_file: "complete-insert", short_option_can_be_strung: false, description: "Use complete INSERT statements that include column names\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Create options", long_option: "--create-options", short_option: "", option_file: "create-options", short_option_can_be_strung: false, description: "Include all MySQL-specific table options in CREATE TABLE statements", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Databases", long_option: "--databases", short_option: "-B", option_file: "databases", short_option_can_be_strung: false, description: "Dump several databases", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Debug", long_option: "--debug[=debug_options]", short_option: "", option_file: "debug", short_option_can_be_strung: false, description: "Write a debugging log", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Debug check", long_option: "--debug-check", short_option: "", option_file: "debug-check", short_option_can_be_strung: false, description: "Print debugging information when the program exits\r\n", status: nil, tool_id: 1, introduced_version: "5.1.21", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Debug info", long_option: "--debug-info", short_option: "", option_file: "debug-info", short_option_can_be_strung: false, description: "Print debugging information, memory and CPU statistics when the program exits", status: nil, tool_id: 1, introduced_version: "5.1.14", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Default character set", long_option: "--default-character-set=character_set", short_option: "", option_file: "default-character-set", short_option_can_be_strung: false, description: "Use charset_name as the default character set", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Apply slave statements", long_option: "--apply-slave-statements", short_option: "", option_file: "apply-slave-statements", short_option_can_be_strung: false, description: "Include STOP SLAVE prior to CHANGE MASTER statement and START SLAVE at end of output", status: nil, tool_id: 1, introduced_version: "5.5.3", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Default auth", long_option: "--default-auth=plugin", short_option: "", option_file: "default-auth=plugin", short_option_can_be_strung: false, description: "The authentication plugin to use", status: nil, tool_id: 1, introduced_version: "5.5.9", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Delayed insert", long_option: "--delayed-insert", short_option: "", option_file: "delayed-insert", short_option_can_be_strung: false, description: "Write INSERT DELAYED statements rather than INSERT statements", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: nil, default_value: nil},
  {name: "Delete master logs", long_option: "--delete-master-logs", short_option: "", option_file: "delete-master-logs", short_option_can_be_strung: false, description: "On a master replication server, delete the binary logs after performing the dump operation", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "On a master replication server, delete the binary logs by sending a PURGE BINARY LOGS statement to the server after performing the dump operation. This option automatically enables --master-data. ", default_value: "false"},
  {name: "Disable keys", long_option: "--disable-keys", short_option: "", option_file: "disable-keys", short_option_can_be_strung: false, description: "For each table, surround the INSERT statements with statements to disable and enable keys", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "For each table, surround the INSERT statements with /*!40000 ALTER TABLE tbl_name DISABLE KEYS */; and /*!40000 ALTER TABLE tbl_name ENABLE KEYS */; statements. This makes loading the dump file faster because the indexes are created after all rows are inserted. This option is effective only for nonunique indexes of MyISAM tables. It has no effect for other tables. ", default_value: "true"},
  {name: "Dump date", long_option: "--dump-date", short_option: "", option_file: "dump-date", short_option_can_be_strung: false, description: "Include dump date as \"Dump completed on\" comment if --comments is given\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "If the --comments option is given, mysqldump produces a comment at the end of the dump of the following form:\r\n\r\n-- Dump completed on DATE\r\nHowever, the date causes dump files taken at different times to appear to be different, even if the data are otherwise identical. --dump-date and --skip-dump-date control whether the date is added to the comment. The default is --dump-date (include the date in the comment). --skip-dump-date suppresses date printing.", default_value: nil},
  {name: "Dump slave", long_option: "--dump-slave[=value]", short_option: "", option_file: "dump-slave", short_option_can_be_strung: false, description: "Include CHANGE MASTER statement that lists binary log coordinates of slave's master", status: nil, tool_id: 1, introduced_version: "5.5.3", removed_version: "", extended_description: "This option is similar to --master-data except that it is used to dump a replication slave server to produce a dump file that can be used to set up another server as a slave that has the same master as the dumped server. It causes the dump output to include a CHANGE MASTER TO statement that indicates the binary log coordinates (file name and position) of the dumped slave's master. These are the master server coordinates from which the slave should start replicating.\r\n\r\n--dump-slave causes the coordinates from the master to be used rather than those of the dumped server, as is done by the --master-data option. In addition, specfiying this option causes the --master-data option to be overridden, if used, and effectively ignored.\r\n\r\nThe option value is handled the same way as for --master-data (setting no value or 1 causes a CHANGE MASTER TO statement to be written to the dump, setting 2 causes the statement to be written but encased in SQL comments) and has the same effect as --master-data in terms of enabling or disabling other options and in how locking is handled.\r\n\r\nThis option causes mysqldump to stop the slave SQL thread before the dump and restart it again after.\r\n\r\nIn conjunction with --dump-slave, the --apply-slave-statements and --include-master-host-port options can also be used.\r\n\r\nThis option was added in MySQL 5.5.3.", default_value: nil},
  {name: "Events", long_option: "--events", short_option: "-E", option_file: "event", short_option_can_be_strung: false, description: "Dump events from the dumped databases", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Include Event Scheduler events for the dumped databases in the output.\r\n", default_value: nil},
  {name: "Extended insert", long_option: "--extended-insert", short_option: "-e", option_file: "extended-insert", short_option_can_be_strung: false, description: "Use multiple-row INSERT syntax that include several VALUES lists", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Use multiple-row INSERT syntax that include several VALUES lists. This results in a smaller dump file and speeds up inserts when the file is reloaded.", default_value: "true"},
  {name: "Fields enclosed by", long_option: "--fields-enclosed-by=string", short_option: "", option_file: "fields-enclosed-by", short_option_can_be_strung: false, description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "These options are used with the --tab option and have the same meaning as the corresponding FIELDS clauses for LOAD DATA INFILE. See Section 13.2.6, “LOAD DATA INFILE Syntax”.", default_value: nil},
  {name: "Fields espaced by", long_option: "--fields-escaped-by=string", short_option: "", option_file: "fields-escaped-by", short_option_can_be_strung: false, description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", default_value: nil},
  {name: "Fields optionally enclosed by", long_option: "--fields-optionally-enclosed=string", short_option: "", option_file: "fields-optionally-enclosed-by", short_option_can_be_strung: false, description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "These options are used with the --tab option and have the same meaning as the corresponding FIELDS clauses for LOAD DATA INFILE. See Section 13.2.6, “LOAD DATA INFILE Syntax”.", default_value: nil},
  {name: "Fields terminated by", long_option: "--fields-terminated-by=string", short_option: "", option_file: "fields-terminated-by", short_option_can_be_strung: false, description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "These options are used with the --tab option and have the same meaning as the corresponding FIELDS clauses for LOAD DATA INFILE. See Section 13.2.6, “LOAD DATA INFILE Syntax”.", default_value: nil},
  {name: "First slave", long_option: "--first-slave", short_option: "", option_file: "first-slave", short_option_can_be_strung: false, description: "Deprecated; use --lock-all-tables instead\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "5.5.3", extended_description: "Deprecated. Use --lock-all-tables instead. --first-slave was removed in MySQL 5.5.3.\r\n", default_value: nil},
  {name: "Flush logs", long_option: "--flush-logs", short_option: "-F", option_file: "flush-logs", short_option_can_be_strung: false, description: "Flush the MySQL server log files before starting the dump\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Flush the MySQL server log files before starting the dump. This option requires the RELOAD privilege. If you use this option in combination with the --all-databases option, the logs are flushed for each database dumped. The exception is when using --lock-all-tables, --master-data, or (as of MySQL 5.5.21) --single-transaction: In this case, the logs are flushed only once, corresponding to the moment that all tables are locked. If you want your dump and the log flush to happen at exactly the same moment, you should use --flush-logs together with --lock-all-tables, --master-data, or --single-transaction.", default_value: nil},
  {name: "Flush privileges", long_option: "--flush-privileges", short_option: "", option_file: "flush-privileges", short_option_can_be_strung: false, description: "Emit a FLUSH PRIVILEGES statement after dumping the mysql database\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Add a FLUSH PRIVILEGES statement to the dump output after dumping the mysql database. This option should be used any time the dump contains the mysql database and any other database that depends on the data in the mysql database for proper restoration.", default_value: nil},
  {name: "Help", long_option: "--help", short_option: "", option_file: "", short_option_can_be_strung: false, description: "Display help message and exit\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: nil},
  {name: "Hex blob", long_option: "--hex-blob", short_option: "", option_file: "hex-blob", short_option_can_be_strung: false, description: "Dump binary columns using hexadecimal notation (for example, 'abc' becomes 0x616263)\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Dump binary columns using hexadecimal notation (for example, 'abc' becomes 0x616263). The affected data types are BINARY, VARBINARY, the BLOB types, and BIT.\r\n", default_value: nil},
  {name: "Host", long_option: "--host=host_name", short_option: "-h host_name", option_file: "host", short_option_can_be_strung: false, description: "Host to connect to (IP address or hostname)\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Dump data from the MySQL server on the given host. The default host is localhost.", default_value: nil},
  {name: "Ignore table", long_option: "--ignore-table=db_name.tbl_name", short_option: "", option_file: "ignore-table", short_option_can_be_strung: false, description: "Do not dump the given table\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Do not dump the given table, which must be specified using both the database and table names. To ignore multiple tables, use this option multiple times. This option also can be used to ignore views.\r\n", default_value: nil},
  {name: "Include master host port", long_option: "--include-master-host-port", short_option: "", option_file: "include-master-host-port", short_option_can_be_strung: false, description: "Include MASTER_HOST/MASTER_PORT options in CHANGE MASTER statement produced with --dump-slave\r\n", status: nil, tool_id: 1, introduced_version: "5.5.3", removed_version: "", extended_description: "For the CHANGE MASTER TO statement in a slave dump produced with the --dump-slave option, add MASTER_HOST and MASTER_PORT options for the host name and TCP/IP port number of the slave's master. This option was added in MySQL 5.5.3.\r\n", default_value: nil},
  {name: "Insert ignore", long_option: "--insert-ignore", short_option: "", option_file: "insert-ignore", short_option_can_be_strung: false, description: "Write INSERT IGNORE statements rather than INSERT statements\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Write INSERT IGNORE statements rather than INSERT statements.\r\n", default_value: nil},
  {name: "Lines terminated by", long_option: "--lines-terminated-by=string", short_option: "", option_file: "lines-terminated-by", short_option_can_be_strung: false, description: "This option is used with the --tab option and has the same meaning as the corresponding clause for LOAD DATA INFILE\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option is used with the --tab option and has the same meaning as the corresponding LINES clause for LOAD DATA INFILE. See Section 13.2.6, “LOAD DATA INFILE Syntax”.\r\n", default_value: nil},
  {name: "Lock all tables", long_option: "--lock-tall-tables", short_option: "-x", option_file: "lock-all-tables", short_option_can_be_strung: false, description: "Lock all tables across all databases\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Lock all tables across all databases. This is achieved by acquiring a global read lock for the duration of the whole dump. This option automatically turns off --single-transaction and --lock-tables.\r\n", default_value: nil},
  {name: "Lock tables", long_option: "--lock-tables", short_option: "-l", option_file: "lock-tables", short_option_can_be_strung: false, description: "Lock all tables before dumping them\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "For each dumped database, lock all tables to be dumped before dumping them. The tables are locked with READ LOCAL to permit concurrent inserts in the case of MyISAM tables. For transactional tables such as InnoDB, --single-transaction is a much better option than --lock-tables because it does not need to lock the tables at all.\r\n\r\nBecause --lock-tables locks tables for each database separately, this option does not guarantee that the tables in the dump file are logically consistent between databases. Tables in different databases may be dumped in completely different states.", default_value: "true"},
  {name: "Log error", long_option: "--log-error=file_name", short_option: "", option_file: "log-error", short_option_can_be_strung: false, description: "Append warnings and errors to the named file\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Log warnings and errors by appending them to the named file. The default is to do no logging.\r\n", default_value: nil},
  {name: "Master data", long_option: "--master-data[=value]", short_option: "", option_file: "master-data", short_option_can_be_strung: false, description: "Write the binary log file name and position to the output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Use this option to dump a master replication server to produce a dump file that can be used to set up another server as a slave of the master. It causes the dump output to include a CHANGE MASTER TO statement that indicates the binary log coordinates (file name and position) of the dumped server. These are the master server coordinates from which the slave should start replicating after you load the dump file into the slave.\r\n\r\nIf the option value is 2, the CHANGE MASTER TO statement is written as an SQL comment, and thus is informative only; it has no effect when the dump file is reloaded. If the option value is 1, the statement is not written as a comment and takes effect when the dump file is reloaded. If no option value is specified, the default value is 1.\r\n\r\nThis option requires the RELOAD privilege and the binary log must be enabled.\r\n\r\nThe --master-data option automatically turns off --lock-tables. It also turns on --lock-all-tables, unless --single-transaction also is specified, in which case, a global read lock is acquired only for a short time at the beginning of the dump (see the description for --single-transaction). In all cases, any action on logs happens at the exact moment of the dump.\r\n\r\nIt is also possible to set up a slave by dumping an existing slave of the master. In MySQL 5.5.3 and later, you can create such a dump using the --dump-slave option, which overrides --master-data and causes it to be ignored if both options are used.", default_value: nil},
  {name: "Max allowed packet", long_option: "--max-allowed-packet=value", short_option: "", option_file: "max-allowed-packet", short_option_can_be_strung: false, description: "The maximum packet length to send to or receive from the server\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "The maximum size of the buffer for client/server communication. The default is 24MB, the maximum is 1GB.\r\n", default_value: "25165824"},
  {name: "Net buffer length", long_option: "--net-buffer-length=value", short_option: "", option_file: "net-buffer-length", short_option_can_be_strung: false, description: "The buffer size for TCP/IP and socket communication\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: "1046528"},
  {name: "No autocommit", long_option: "--no-autocommit", short_option: "", option_file: "no-autocommit", short_option_can_be_strung: false, description: "Enclose the INSERT statements for each dumped table within SET autocommit = 0 and COMMIT statements\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Enclose the INSERT statements for each dumped table within SET autocommit = 0 and COMMIT statements.\r\n", default_value: "false"},
  {name: "No create db", long_option: "--no-create-db", short_option: "-n", option_file: "no-create-db", short_option_can_be_strung: false, description: "This option suppresses the CREATE DATABASE statements\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option suppresses the CREATE DATABASE statements that are otherwise included in the output if the --databases or --all-databases option is given.\r\n", default_value: "false"},
  {name: "No create info", long_option: "--no-create-info", short_option: "-t", option_file: "no-create-info", short_option_can_be_strung: false, description: "Do not write CREATE TABLE statements that re-create each dumped table\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Do not write CREATE TABLE statements that re-create each dumped table.\r\n\r\nThis option does not not exclude statements creating log file groups or tablespaces from mysqldump output; however, you can use the --no-tablespaces option for this purpose.", default_value: "false"},
  {name: "No data", long_option: "--no-data", short_option: "-d", option_file: "no-data", short_option_can_be_strung: false, description: "Do not dump table contents\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Do not write any table row information (that is, do not dump table contents). This is useful if you want to dump only the CREATE TABLE statement for the table (for example, to create an empty copy of the table by loading the dump file).", default_value: "false"},
  {name: "No set names", long_option: "--no-set-names", short_option: "-N", option_file: "no-set-names", short_option_can_be_strung: false, description: "Same as --skip-set-charset\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This has the same effect as --skip-set-charset.", default_value: ""},
  {name: "No tablespaces", long_option: "--no-tablespaces", short_option: "-y", option_file: "no-tablespaces", short_option_can_be_strung: false, description: "Do not write any CREATE LOGFILE GROUP or CREATE TABLESPACE statements in output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option suppresses all CREATE LOGFILE GROUP and CREATE TABLESPACE statements in the output of mysqldump.", default_value: ""},
  {name: "Opt", long_option: "--opt", short_option: "", option_file: "opt", short_option_can_be_strung: false, description: "Shorthand for --add-drop-table --add-locks --create-options --disable-keys --extended-insert --lock-tables --quick --set-charset.\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option is shorthand. It is the same as specifying --add-drop-table --add-locks --create-options --disable-keys --extended-insert --lock-tables --quick --set-charset. It should give you a fast dump operation and produce a dump file that can be reloaded into a MySQL server quickly.\r\n\r\nThe --opt option is enabled by default. Use --skip-opt to disable it. See the discussion at the beginning of this section for information about selectively enabling or disabling a subset of the options affected by --opt.", default_value: ""},
  {name: "Order by primary", long_option: "--order-by-primary", short_option: "", option_file: "order-by-primary", short_option_can_be_strung: false, description: "Dump each table's rows sorted by its primary key, or by its first unique index\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Dump each table's rows sorted by its primary key, or by its first unique index, if such an index exists. This is useful when dumping a MyISAM table to be loaded into an InnoDB table, but will make the dump operation take considerably longer.\r\n", default_value: "false"},
  {name: "Password", long_option: "--password[=password]", short_option: "-p[password]", option_file: "password", short_option_can_be_strung: false, description: "The password to use when connecting to the server\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "The password to use when connecting to the server. If you use the short option form (-p), you cannot have a space between the option and the password. If you omit the password value following the --password or -p option on the command line, mysqldump prompts for one.\r\n\r\nSpecifying a password on the command line should be considered insecure. See Section 6.1.2.1, “End-User Guidelines for Password Security”. You can use an option file to avoid giving the password on the command line.", default_value: ""},
  {name: "Pipe", long_option: "--pipe", short_option: "-W", option_file: "", short_option_can_be_strung: false, description: "On Windows, connect to server using a named pipe\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "On Windows, connect to the server using a named pipe. This option applies only if the server supports named-pipe connections.", default_value: ""},
  {name: "Plugin dir", long_option: "--plugin-dir=path", short_option: "", option_file: "", short_option_can_be_strung: false, description: "The directory where plugins are located\r\n", status: nil, tool_id: 1, introduced_version: "5.5.9", removed_version: "", extended_description: "The directory in which to look for plugins. It may be necessary to specify this option if the --default-auth option is used to specify an authentication plugin but mysqldump does not find it. See Section 6.3.6, “Pluggable Authentication”.\r\n\r\nThis option was added in MySQL 5.5.9.", default_value: ""},
  {name: "Port", long_option: "--port=port_num", short_option: "-P port_num", option_file: "port", short_option_can_be_strung: false, description: "The TCP/IP port number to use for the connection\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "The TCP/IP port number to use for the connection.", default_value: "0"},
  {name: "Protocol", long_option: "--protocol=type", short_option: "", option_file: "protocol", short_option_can_be_strung: false, description: "The connection protocol to use\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "The connection protocol to use for connecting to the server. It is useful when the other connection parameters normally would cause a protocol to be used other than the one you want. For details on the permissible values, see Section 4.2.2, “Connecting to the MySQL Server”.", default_value: ""},
  {name: "Quick", long_option: "--quick", short_option: "-q", option_file: "quick", short_option_can_be_strung: false, description: "Retrieve rows for a table from the server a row at a time\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option is useful for dumping large tables. It forces mysqldump to retrieve rows for a table from the server a row at a time rather than retrieving the entire row set and buffering it in memory before writing it out.\r\n", default_value: ""},
  {name: "Quote names", long_option: "--quote-names", short_option: "-Q", option_file: "quote-names", short_option_can_be_strung: false, description: "Quote identifiers within backtick characters\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Quote identifiers (such as database, table, and column names) within “`” characters. If the ANSI_QUOTES SQL mode is enabled, identifiers are quoted within “\"” characters. This option is enabled by default. It can be disabled with --skip-quote-names, but this option should be given after any option such as --compatible that may enable --quote-names.", default_value: ""},
  {name: "Replace", long_option: "--replace", short_option: "", option_file: "replace", short_option_can_be_strung: false, description: "Write REPLACE statements rather than INSERT statements\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Write REPLACE statements rather than INSERT statements.", default_value: "false"},
  {name: "Result file", long_option: "--result-file=file_name", short_option: "-r file_name", option_file: "result-file", short_option_can_be_strung: false, description: "Direct output to a given file\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Direct output to a given file. This option should be used on Windows to prevent newline “\\n” characters from being converted to “\\r\\n” carriage return/newline sequences. The result file is created and its previous contents overwritten, even if an error occurs while generating the dump.\r\n", default_value: ""},
  {name: "Routines", long_option: "--routines", short_option: "-R", option_file: "routines", short_option_can_be_strung: false, description: "Dump stored routines (procedures and functions) from the dumped databases\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Include stored routines (procedures and functions) for the dumped databases in the output. Use of this option requires the SELECT privilege for the mysql.proc table. The output generated by using --routines contains CREATE PROCEDURE and CREATE FUNCTION statements to re-create the routines. However, these statements do not include attributes such as the routine creation and modification timestamps. This means that when the routines are reloaded, they will be created with the timestamps equal to the reload time.\r\n\r\nIf you require routines to be re-created with their original timestamp attributes, do not use --routines. Instead, dump and reload the contents of the mysql.proc table directly, using a MySQL account that has appropriate privileges for the mysql database.\r\n\r\nPrior to MySQL 5.5.21, this option had no effect when used together with the --xml option. (Bug #11760384, Bug #52792)", default_value: "false"},
  {name: "Set charset", long_option: "--set-charset", short_option: "", option_file: "set-charset", short_option_can_be_strung: false, description: "Add SET NAMES default_character_set to output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Add SET NAMES default_character_set to the output. This option is enabled by default. To suppress the SET NAMES statement, use --skip-set-charset.Add SET NAMES default_character_set to output\r\n", default_value: "true"},
  {name: "Single transaction", long_option: "--single-transaction", short_option: "", option_file: "single-transaction", short_option_can_be_strung: false, description: "This option issues a BEGIN SQL statement before dumping data from the server\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option sets the transaction isolation mode to REPEATABLE READ and sends a START TRANSACTION SQL statement to the server before dumping data. It is useful only with transactional tables such as InnoDB, because then it dumps the consistent state of the database at the time when START TRANSACTION was issued without blocking any applications.\r\n\r\nWhen using this option, you should keep in mind that only InnoDB tables are dumped in a consistent state. For example, any MyISAM or MEMORY tables dumped while using this option may still change state.\r\n\r\nWhile a --single-transaction dump is in process, to ensure a valid dump file (correct table contents and binary log coordinates), no other connection should use the following statements: ALTER TABLE, CREATE TABLE, DROP TABLE, RENAME TABLE, TRUNCATE TABLE. A consistent read is not isolated from those statements, so use of them on a table to be dumped can cause the SELECT that is performed by mysqldump to retrieve the table contents to obtain incorrect contents or fail.\r\n\r\nThe --single-transaction option and the --lock-tables option are mutually exclusive because LOCK TABLES causes any pending transactions to be committed implicitly.\r\n\r\nThis option is not supported for MySQL Cluster tables; the results cannot be guaranteed to be consistent due to the fact that the NDBCLUSTER storage engine supports only the READ_COMMITTED transaction isolation level. You should always use NDB backup and restore instead.\r\n\r\nTo dump large tables, combine the --single-transaction option with the --quick option.", default_value: "false"},
  {name: "Skip add drop table", long_option: "--skip-add-drop-table", short_option: "", option_file: "skip-add-drop-table", short_option_can_be_strung: false, description: "Do not add a DROP TABLE statement before each CREATE TABLE statement\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip add locks", long_option: "--skip-add-locks", short_option: "", option_file: "skip-add-locks", short_option_can_be_strung: false, description: "Do not add locks\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip comments", long_option: "--skip-comments", short_option: "", option_file: "skip-comments", short_option_can_be_strung: false, description: "Do not add comments to the dump file\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip compact", long_option: "--skip-compact", short_option: "", option_file: "skip-compact", short_option_can_be_strung: false, description: "Do not produce more compact output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip disable keys", long_option: "--skip-disable-keys", short_option: "", option_file: "skip-disable-keys", short_option_can_be_strung: false, description: "Do not disable keys\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip extended insert", long_option: "--skip-extended-insert", short_option: "", option_file: "skip-extended-insert", short_option_can_be_strung: false, description: "Turn off extended-insert\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip opt", long_option: "--skip-opt", short_option: "", option_file: "skip-opt", short_option_can_be_strung: false, description: "Turn off the options set by --opt\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip quick", long_option: "--skip-quick", short_option: "", option_file: "skip-quick", short_option_can_be_strung: false, description: "Do not retrieve rows for a table from the server a row at a time\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip quote names", long_option: "--skip-quote-names", short_option: "", option_file: "skip-quote-names", short_option_can_be_strung: false, description: "Do not quote identifiers\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip set charset", long_option: "--skip-set-charset", short_option: "", option_file: "skip-set-charset", short_option_can_be_strung: false, description: "Suppress the SET NAMES statement\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip triggers", long_option: "--skip-triggers", short_option: "", option_file: "skip-triggers", short_option_can_be_strung: false, description: "Do not dump triggers\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Skip tz utc", long_option: "--skip-tz-utc", short_option: "", option_file: "skip-tz-utc", short_option_can_be_strung: false, description: "Turn off tz-utc\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Socket", long_option: "--socket=path", short_option: "-S path", option_file: "socket", short_option_can_be_strung: false, description: "For connections to localhost\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Tab", long_option: "--tab=path", short_option: "-T path", option_file: "tab", short_option_can_be_strung: false, description: "Produce tab-separated data files\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " Produce tab-separated text-format data files. For each dumped table, mysqldump creates a tbl_name.sql file that contains the CREATE TABLE statement that creates the table, and the server writes a tbl_name.txt file that contains its data. The option value is the directory in which to write the files.\r\nNote\r\n\r\nThis option should be used only when mysqldump is run on the same machine as the mysqld server. You must have the FILE privilege, and the server must have permission to write files in the directory that you specify.\r\n\r\nBy default, the .txt data files are formatted using tab characters between column values and a newline at the end of each line. The format can be specified explicitly using the --fields-xxx and --lines-terminated-by options.\r\n\r\nColumn values are converted to the character set specified by the --default-character-set option. ", default_value: ""},
  {name: "Tables", long_option: "--tables", short_option: "", option_file: "tables", short_option_can_be_strung: false, description: "Override the --databases or -B option\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " Override the --databases or -B option. mysqldump regards all name arguments following the option as table names. ", default_value: ""},
  {name: "Triggers", long_option: "--triggers", short_option: "", option_file: "triggers", short_option_can_be_strung: false, description: "Dump triggers for each dumped table\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " Include triggers for each dumped table in the output. This option is enabled by default; disable it with --skip-triggers. ", default_value: "true"},
  {name: "Tz UTC", long_option: "--tz-utc", short_option: "", option_file: "tz-utc", short_option_can_be_strung: false, description: "Add SET TIME_ZONE='+00:00' to the dump file\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "This option enables TIMESTAMP columns to be dumped and reloaded between servers in different time zones. mysqldump sets its connection time zone to UTC and adds SET TIME_ZONE='+00:00' to the dump file. Without this option, TIMESTAMP columns are dumped and reloaded in the time zones local to the source and destination servers, which can cause the values to change if the servers are in different time zones. --tz-utc also protects against changes due to daylight saving time. --tz-utc is enabled by default. To disable it, use --skip-tz-utc. ", default_value: "true"},
  {name: "User", long_option: "--user=user_name", short_option: "-u user_name", option_file: "user", short_option_can_be_strung: false, description: "MySQL user name to use when connecting to server\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " The MySQL user name to use when connecting to the server. ", default_value: ""},
  {name: "Verbose", long_option: "--verbose", short_option: "-v", option_file: "", short_option_can_be_strung: false, description: "Verbose mode\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " Verbose mode. Print more information about what the program does. ", default_value: "false"},
  {name: "Version", long_option: "--version", short_option: "-V", option_file: "", short_option_can_be_strung: false, description: "Display version information and exit\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: " Display version information and exit. ", default_value: ""},
  {name: "Where", long_option: "--where='where_condition'", short_option: "-w 'where_condition'", option_file: "where", short_option_can_be_strung: false, description: "Dump only rows selected by the given WHERE condition\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "Dump only rows selected by the given WHERE condition. Quotes around the condition are mandatory if it contains spaces or other characters that are special to your command interpreter.\r\n\r\nExamples:\r\n\r\n--where=\"user='jimf'\"\r\n-w\"userid>1\"\r\n-w\"userid<1\"\r\n", default_value: ""},
  {name: "XML", long_option: "--xml", short_option: "-X", option_file: "xml", short_option_can_be_strung: false, description: "Produce XML output\r\n", status: nil, tool_id: 1, introduced_version: "", removed_version: "", extended_description: "", default_value: ""},
  {name: "Login path", long_option: "--loginpath=name", short_option: "", option_file: "", short_option_can_be_strung: false, description: "Read login path options from .mylogin.cnf", status: nil, tool_id: 1, introduced_version: "5.66", removed_version: "", extended_description: "Read options from the named login path in the .mylogin.cnf login file. A “login path” is an option group that permits only a limited set of options: host, user, and password. Think of a login path as a set of values that indicate the server host and the credentials for authenticating with the server. To create the login file, use the mysql_config_editor utility. See Section 4.6.6, “mysql_config_editor — MySQL Configuration Utility”. This option was added in MySQL 5.6.6. ", default_value: ""},
  {name: "Secure Auth", long_option: "--secure-auth", short_option: "", option_file: "secure-auth", short_option_can_be_strung: false, description: "Do not send passwords to the server in old (pre-4.1.1) format", status: nil, tool_id: 1, introduced_version: "5.6.17", removed_version: "", extended_description: "Do not send passwords to the server in old (pre-4.1) format. This prevents connections except for servers that use the newer password format. This option is enabled by default; use --skip-secure-auth to disable it. This option was added in MySQL 5.6.17. ", default_value: ""},
  {name: "Set GTID purged", long_option: "--set-gtid-purged=value", short_option: "", option_file: "set-gtid-purged", short_option_can_be_strung: false, description: "Whether to add SET @@GLOBAL.GTID_PURGED to output", status: nil, tool_id: 1, introduced_version: "5.6.9", removed_version: "", extended_description: " This option enables control over global transaction ID (GTID) information written to the dump file, by indicating whether to add a SET @@global.gtid_purged statement to the output.\r\n\r\nThe following table shows the permitted option values. The default value is AUTO.\r\nValue\tMeaning\r\nOFF\tAdd no SET statement to the output.\r\nON\tAdd a SET statement to the output. An error occurs if GTIDs are not enabled on the server.\r\nAUTO\tAdd a SET statement to the output if GTIDs are enabled on the server.\r\n\r\nThis option was added in MySQL 5.6.9. ", default_value: ""}
])
